import React, {Component} from 'react';

import './product-list-item.css';

export default class ProductListItem extends Component {
    
    state = {
        quantity: 1
    };

    QuantityChange = (e) => {
        this.setState({
            quantity: e.target.value
        });
        this.props.onQuantityChange(e.id, this.state.quantity);
    }
    
    render(){

        const { id, name, price , 
            onQuantityChange, quantity} = this.props;

        return (
            <tr>
                <td>
                    {id}
                </td>
                <td>
                    {name}
                </td>
                <td>
                    <select id={id} className="form-control"
                    onChange={QuantityChange}>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                    </select>
                </td>
                <td>
                    {price}  
                </td>
                <td>
                <select className="form-control">
                        <option>0</option>
                        <option>10</option>
                        <option>25</option>
                    </select>
                </td>
                <td>
                    {quantity*price}
                </td>
            </tr>
        )
    }
}
