import React from 'react';

import ProductListItem from '../product-list-item';
import './product-list.css';

const ProductList = ({prods, onQuantityChange}) => {
    const elements = prods.map((item) => {
        const {newValue, ...itemProps} = item;

    return (
            <ProductListItem key = {prods.id} {...itemProps}
            onQuantityChange={() => onQuantityChange(prods.id, newValue)} />
    );
    }); 

        return (
            <tbody>
                {elements}
            </tbody>        
    );    
    }

    export default ProductList;
