import React, { Component } from 'react';

import ProductAddForm from '../add-product-form';
import ProductList from '../product-list';

import './App.css';

class App extends Component {

  Id = 1;

  state = {
    productData : [
      this.createItem('Smartphone', 1, 100),
      this.createItem('Tablet PC', 1, 200),
      this.createItem('Laptop', 1, 250)
    ],
    total: 550
  };

  createItem(name, quantity, price) {
    return {
      name,
      quantity,
      price,
      summary: price*quantity,
      discount: 0,
      id: this.Id++ 
    };
  };

  addItem = (name, quantity, price) => {
    const newItem = this.createItem(name, quantity, price);

    this.setState(({ productData })=> {


      const newArr = [...productData,
                        newItem];
      return { 
        productData: newArr
      };
    });
  }

    toggleProperty(arr, id, propName, newValue, summary){
      console.log('id is ', id);
      console.log('new value is ', newValue);
      console.log('total is ', this.state.total);
      console.log('summary is ', summary);
      const idx = arr.findIndex((el) => el.id === id);

        const oldItem = arr[idx];
        const newItem = {...oldItem, [propName]: newValue, [summary]: summary};
        return [
          ...arr.slice(0, idx),
          newItem, 
          ...arr.slice(idx + 1)];        
    };

    onQuantityChange = (id, newValue, summary) => {
      this.setState(({productData}) => {
        return {
          productData: this.toggleProperty(productData, id, 
            'quantity', newValue, summary),

          total: this.getNewTotal(productData)
        }


      });
    };



    deleteItem = (id) => {
      this.setState(({productData}) => {
        const idx = productData.findIndex((el) => el.id === id);

        const newArray = [
          ...productData.slice(0, idx), 
          ...productData.slice(idx + 1)];


        return {
          productData: newArray,
          total: this.getNewTotal(productData)

        };
      });
    };

    getNewTotal(array)
      { const initialvalue = 0;
        let total = () => array.reduce((acc,cv) => 
         acc + cv.summary,  initialvalue
        );
        return total;
      }


  render() {

    const { productData, total} = this.state;
    return (
      <div className="shopcart-app">
          <ProductAddForm onItemAdded={this.addItem}/>  


      <table className="table">
        <thead className="thead-dark">
          <tr>
            <th width="25%">Product Name</th>
            <th width="15%">Quantity</th>
            <th width="10%">Price</th>
            <th width="15%">Discount</th>
            <th width="15%">Summary</th>
            <th width="15%"></th>
          </tr>
        </thead>
        <tbody>
          <ProductList prods={productData}
          onDiscountChange={this.onDiscountChange}
          onQuantityChange={this.onQuantityChange}
          onDeleted={this.deleteItem}/>
          <tr>
                    <td colSpan="4" align="right">
                        <b>Total:</b>
                    </td>
                    <td>
                        {total} 
                    </td>
                </tr>
        </tbody>
        </table>


      </div>
    );
  }
}

export default App;
