import React, {Component} from 'react';

import './product-list-item.css';

export default class ProductListItem extends Component {

    render(){

        state = {
            quantity: 1,
            discount: 0.0
        };
        const { name, price} = this.props;

        const id = 1;

        const quantity = 1;

        return (
            <tr>
                <td>
                    {id}
                </td>
                <td>
                    {name}
                </td>
                <td>
                    <select className="form-control">
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                    </select>
                </td>
                <td>
                    {price}  
                </td>
                <td>
                <select className="form-control">
                        <option>0</option>
                        <option>10</option>
                        <option>25</option>
                    </select>
                </td>
                <td>
                    {quantity*price}
                </td>
            </tr>
        )
    }
}
