import React, { Component } from 'react';

import ProductAddForm from '../add-product-form';
import ProductList from '../product-list';

import './App.css';

class App extends Component {

  maxId = 100;

  state = {
    product : [
      this.createItem('Smartphone', 100),
      this.createItem('Laptop', 250),
      this.createItem('Tablet PC', 100)
    ],
    quantity,
    discount
  };

  createItem(name, price) {
    return {
      name,
      price,
      discount: 0.0,
      quantity: 1,
      id: this.maxId++ 
    };
  };

  addItem = (name, price) => {
    const newItem = this.createTodoItem(name, price);

    this.setState(({ product })=> {


      const newArr = [...product,
                        newItem];
      return { 
        product: newArr
      };
    });

  };

  render() {
    return (
      <div className="App">
          <ProductAddForm/>  


      <table className="table">
        <thead className="thead-dark">
          <th scope="col">#</th>
          <th scope="col">Product Name</th>
          <th scope="col">Quantity</th>
          <th scope="col">Price</th>
          <th scope="col">Discount</th>
          <th scope="col">Summary</th>
        </thead>
        <tbody>
          <ProductList products={product}/>
        </tbody>
      </table>


      </div>
    );
  }
}

export default App;
