import React, {Component} from 'react';

import './product-list-item.css';

export default class ProductListItem extends Component {
    
    state = {
        name : '',
        price: 0,
        quantity: 1,
        discount: 0,
        summary: 0
    };

    updateQuantity = (id, value) => {
        this.props.onQuantityChange(id,value)
    };

    QuantityChange = (e) => {
        this.setState({
            quantity: e.target.value,
            id: e.target.id,
            summary: this.countSum(e.target.id,e.target.value)
        })
        };
    
    countSum(price,quantity) {
        return quantity*price;
    }
        
        DiscountChange = (e) => {
            this.setState({
                discount: e.target.value,
                id: e.target.id
            })
        };
        
    updateState(name, price, quantity) {
        this.setState({
            name: name,
            price: price,
            quantity: quantity,
            summary: this.countSum(price,quantity)
        })
    }
    
    
    render(){
        
        const {id, name, price, 
            onQuantityChange, onDeleted, quantity, summary} = this.props;

        const sumWithoutDisc = (this.state.quantity*price);

        const sumWithDisc = sumWithoutDisc - sumWithoutDisc*this.state.discount;

        this.updateState(name,price,quantity);
        
        return (
            <tr>
                <td>
                    {this.state.name}
                </td>
                <td>
                    <select className="form-control"
                    onChange={this.QuantityChange}> 
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                    </select>
                </td>
                <td>
                    {this.state.price}  
                </td>
                <td>
                <select className="form-control"
                onChange={this.DiscountChange} value={this.state.discount}>
                        <option value="0">0%</option>
                        <option value="0.1">10%</option>
                        <option value="0.25">25%</option>
                    </select>
                </td>
                <td>
                    {this.state.summary}
                </td>
                <td>
                    <button type="button"
                        className="btn btn-outline-danger btn-sm float-right"
                        onClick={onDeleted}>
                        <i className="fa fa-trash-o" />
                    </button>
                </td>
            </tr>
        )
    }
}
