import React from 'react';

import ProductListItem from '../product-list-item';
import './product-list.css';

const ProductList = ({prods, total, quantity, discount,    
                        onDeleted, onQuantityChange, 
                        onDiscountChange}) => {
    const elements = prods.map((item) => {
        const {id, newQuantity, newDiscount, ...itemProps} = item;
        
    

                    
    return (
            <tr key={id}>
                <ProductListItem {...itemProps}
                onQuantityChange={() => onQuantityChange(id,newQuantity)}
                onDiscountChange={() => onDiscountChange(id, newDiscount)} 
                onDeleted={() => onDeleted(id)}/>
            </tr>
            
            
    );
    });

       return (
            <tbody>
                {elements}
                <tr>
                    <td colSpan="4" align="right">
                        <b>Total:</b>
                    </td>
                    <td>
                        {total} 
                    </td>
                </tr>
            </tbody>
                 
    );    
    }

    export default ProductList;
