import React, {Component} from 'react';

import './add-product-form.css';

export default class ProductAddForm extends Component {
    
    state = {
        label: 'Smartphone',
        quantity: 1,
        price: 100      
        };
    

    onQuantityChange = (e) => {
        this.setState({
            quantity: e.target.value,
        })
    };

    onLabelChange = (e) => {
        this.setState({
            label: e.target.value,
            price: this.setPrice(e.target.value),
        })
    };

    setPrice = (label) => {
        switch(label) {
            case 'Smartphone': return 100
            case 'Laptop': return 250
            case 'Tablet PC': return 200
            default : return 100
        }
    };



    AddItem = (e) => {
        this.props.onItemAdded(this.state.label,
                                this.state.price,
                                this.state.quantity);
    }; 



    render() {
        return (
            <div className="form form-row">
                <div className="select form-group col-md-4">
                <select className="form-control"
                onChange={this.onLabelChange} 
                value={this.state.label}>
                    <option>Smartphone</option>
                    <option>Laptop</option>
                    <option>Tablet PC</option>
                </select>
                </div>
                <div className="form-group col-md-1">
                <select className="form-control"
                    onChange={this.onQuantityChange} 
                    value={this.state.quantity}>>
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                </select>
                </div>
                <div className="form-group col-md-2">
                <button className="btn btn-outline-secondary"
                onClick={this.AddItem}>
                Add product</button>
                </div>
            </div>
        )
    }
}
