import React, {Component} from 'react';

import './product-list-item.css';

export default class ProductListItem extends Component {
    
    state = {
        id: this.props.id,
        price: this.props.price,
        quantity: this.props.quantity,
        discount: this.props.discount,
        summary: this.props.summary 
    }; 

    QuantityChange = (e) => {
/*          this.setState({
            quantity: +e.target.value,
            id: e.target.id,
            summary: this.countSum(this.props.price,e.target.value,
                                        this.state.discount)
        }, () => { */
        this.props.onProductChange(e.target.value, 
            this.countSum(this.props.price,e.target.value,
                this.props.discount), 'quantity');
    };
    

    countSum(price,quantity,discount) {
        return quantity*price - quantity*price*discount;
    }
        
        DiscountChange = (e) => {
/*             this.setState({
                discount: e.target.value,
                id: e.target.id,
                summary: this.countSum(this.props.price,this.state.quantity, 
                                        e.target.value)
            }, () => {
                this.props.onProductChange(this.state.discount, this.state.summary, 'discount') */

                this.props.onProductChange(e.target.value, 
                    this.countSum(this.props.price,
                        this.props.discount, e.target.value), 'discount');
        };
    
        
   
    render(){
        
        // const {} = this.state;
        const {name, onDeleted, cartDiscount, price, quantity, summary, discount} = this.props;

        console.log(discount);

        return (
            <>
                <td>
                    {name}
                </td>
                <td>
                    <input className="form-control" type="number" min ="1" max="100"
                    value={quantity}  
                    onChange={this.QuantityChange}> 
                    </input>
                </td>
                <td>
                    {price}  
                </td>
                <td>
                <select className="form-control"
                onChange={this.DiscountChange} value={discount} 
                            > 
                        <option value="0">0%</option>
                        <option value="0.1">10%</option>
                        <option value="0.25">25%</option>
                    </select>
                </td>
                <td>
                    {summary}
                </td>
                <td>
                    <button type="button"
                        className="btn btn-outline-danger btn-sm float-right"
                        onClick={onDeleted}>
                        <i className="fa fa-trash-o" />
                    </button>
                </td>
            </>
        )
    }
}
