import React, { Component } from 'react';

import ProductAddForm from '../add-product-form';
import ProductList from '../product-list';

import './App.css';

class App extends Component {

  Id = 1;


  state = {
    productData : [
      this.createItem('Smartphone', 100),
      this.createItem('Laptop', 250),
      this.createItem('Tablet PC', 200)
    ],
    quantity : 1,
    discount : 0.0
  };

  createItem(name, price) {
    return {
      name,
      price,
      discount: 0.0,
      quantity: 1,
      id: this.Id++ 
    };
  };

  addItem = (name, price) => {
    const newItem = this.createItem(name, price);

    this.setState(({ product })=> {


      const newArr = [...product,
                        newItem];
      return { 
        product: newArr
      };
    });
  }

    toggleProperty(arr, id, propName, newValue){
      const idx = arr.findIndex((el) => el.id === id);

        const oldItem = arr[idx];
        const newItem = {...oldItem, [propName]: newValue};
        console.log(newValue);
        return [
          ...arr.slice(0, idx),
          newItem, 
          ...arr.slice(idx + 1)];
    };

    onQuantityChange = (id, newValue) => {
      this.setState(({productData}) => {
        return {
          productData: this.toggleProperty(productData, id, 
                                      'quantity', newValue)

        };

      });
    };


  render() {

    const { productData, quantity, discount} = this.state;
    return (
      <div className="shopcart-app">
          <ProductAddForm/>  


      <table className="table">
        <thead className="thead-dark">
          <th scope="col">#</th>
          <th scope="col">Product Name</th>
          <th scope="col">Quantity</th>
          <th scope="col">Price</th>
          <th scope="col">Discount</th>
          <th scope="col">Summary</th>
        </thead>
          <ProductList prods={productData}
          onQuantityChange={this.onQuantityChange}/>
        </table>


      </div>
    );
  }
}

export default App;
