import React, { Component } from 'react';

import ProductAddForm from '../add-product-form';
import ProductList from '../product-list';

import './App.css';

class App extends Component {

  Id = 1;

  state = {
    productData : [
      this.createItem('Smartphone', 1, 100),
      this.createItem('Tablet PC', 1, 200),
      this.createItem('Laptop', 1, 250)
    ],
    total: 550
  };

  createItem(name, quantity, price) {
    return {
      name,
      quantity,
      price,
      summary: price*quantity,
      id: this.Id++ 
    };
  };

  addItem = (name, quantity, price) => {
    const newItem = this.createItem(name, quantity, price);

    this.setState(({ productData })=> {


      const newArr = [...productData,
                        newItem];
      return { 
        productData: newArr
      };
    });
  }

    toggleProperty(arr, id, propName, newValue){
      const idx = arr.findIndex((el) => el.id === id);

        const oldItem = arr[idx];
        const newItem = {...oldItem, [propName]: newValue};
        return [
          ...arr.slice(0, idx),
          newItem, 
          ...arr.slice(idx + 1)];
    };

    onQuantityChange = (id, newValue) => {
      console.log(newValue);
      this.setState(({productData}) => {
        return {
          productData: this.toggleProperty(productData, id, 
                                      'quantity', newValue)


        };

      });
    };

    deleteItem = (id, summary) => {
      this.setState(({productData, total}) => {
        const idx = productData.findIndex((el) => el.id === id);

        const newArray = [
          ...productData.slice(0, idx), 
          ...productData.slice(idx + 1)];


        return {
          productData: newArray,
          total: this.getNewTotal(total, summary)

        };
      });
    };

    getNewTotal(total,summary)
      { return total-summary; }


  render() {

    const { productData, total, summary } = this.state;
    return (
      <div className="shopcart-app">
          <ProductAddForm onItemAdded={this.addItem}/>  


      <table className="table">
        <thead className="thead-dark">
          <tr>
            <th scope="col">Product Name</th>
            <th scope="col">Quantity</th>
            <th scope="col">Price</th>
            <th scope="col">Discount</th>
            <th scope="col">Summary</th>
            <th scope="col"></th>
          </tr>
        </thead>
          <ProductList prods={productData}
          total = {total}
          summary = {summary}

          onQuantityChange={this.onQuantityChange}
          onDeleted={this.deleteItem}/>
        </table>


      </div>
    );
  }
}

export default App;
